1451	{"name": "Territorial Boar", "text": "Whenever a creature with power 4 or greater enters the battlefield under your control, Territorial Boar gets +1/+1 and gains vigilance until end of turn.", "type": "Creature — Boar", "uuid": "9dd9f668-a480-5238-a080-984016c7da22", "power": "2", "types": ["Creature"], "colors": ["G"], "layout": "normal", "mtgoId": 71290, "rulings": [{"date": "2019-01-25", "text": "The entering creature must have power 4 or greater as it enters the battlefield, or Territorial Boar’s ability won’t trigger. Static abilities that raise (or lower) a creature’s power are taken into account. However, you can’t have a creature with power 3 or less enter the battlefield, raise its power with a spell, an activated ability, or a triggered ability, and have Territorial Boar’s ability trigger."}, {"date": "2019-01-25", "text": "If the entering creature’s power changes to 3 or less after it has entered the battlefield, Territorial Boar still gets +1/+1 and gains vigilance."}, {"date": "2019-01-25", "text": "If Territorial Boar’s power is raised to 4 or greater as it enters the battlefield, it will cause its own ability to trigger."}], "manaCost": "{1}{G}", "subtypes": ["Boar"], "printings": ["RNA"], "toughness": "2", "edhrecRank": 14893, "legalities": {"duel": "Legal", "brawl": "Legal", "penny": "Legal", "future": "Legal", "legacy": "Legal", "modern": "Legal", "pauper": "Legal", "pioneer": "Legal", "vintage": "Legal", "historic": "Legal", "standard": "Legal", "commander": "Legal"}, "mtgArenaId": 69273, "supertypes": [], "purchaseUrls": {"tcgplayer": "https://mtgjson.com/links/a4cd9c3a0e2da9a4", "cardmarket": "https://mtgjson.com/links/7e0b4497cac1029c"}, "colorIdentity": ["G"], "scryfallOracleId": "848fc2b8-7208-4415-8be1-add84c780f6e", "convertedManaCost": 2.0}
